                    if (res.status) {
                        countDownHeader();
                        $('#frmOtp')[0].reset();
                    } else {
                        $('#frmOtp .form-control').addClass('error-validate');
                        $('#frmOtp .message-error').text(res.text).show();
                        return false;
                    }
                },
                error: function () {
                    $('#frmOtp .form-control').addClass('error-validate');
                    $('#frmOtp .message-error').text('خطایی رخ داده است. لطفا مجدد تلاش کنید.').show();
                    return false;
                }
            });
        });
